function initializeSliders(){const e=document.querySelectorAll(".gallery-slider");console.log("Found sliders to initialize:",e.length),e.forEach(e=>{if(e.swiper){console.log("Slider already initialized");return}try{const t=new Swiper(e,{slidesPerView:1,loop:!0,spaceBetween:30,navigation:{nextEl:e.querySelector(".swiper-button-next"),prevEl:e.querySelector(".swiper-button-prev")},on:{init:function(){console.log("Swiper initialized successfully!")}}})}catch(e){console.error("Swiper initialization error:",e)}})}if(document.readyState==="loading"?document.addEventListener("DOMContentLoaded",function(){setTimeout(initializeSliders,100)}):setTimeout(initializeSliders,100),window.addEventListener("load",function(){setTimeout(initializeSliders,200)}),typeof MutationObserver!="undefined"){const e=new MutationObserver(function(e){e.forEach(function(e){if(e.addedNodes.length){const t=Array.from(e.addedNodes).some(e=>e.nodeType===1&&(e.classList?.contains("gallery-slider")||e.querySelector?.(".gallery-slider")));t&&setTimeout(initializeSliders,50)}})});e.observe(document.body,{childList:!0,subtree:!0})}